import React, { Component } from 'react'
import Link from 'next/link'

export default class PodcastGrid extends Component {
  render() {
    const {channels} = this.props
    return (
      <div>
        <div className='channels'>
          {channels.map((channel, index) => (
            <Link 
              href={`/[channel]?id=${channel.id}`} key={index}
              as={`${channel.id}`}
            >
              <a className='channel'>
                <img src={channel.urls.logo_image.original} alt='' />
                <h2>{channel.title}</h2>
              </a>
            </Link>
          ))}
        </div>

        <style jsx>{`
          .channels {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(145px, 1fr));
            grid-gap: 10px;
            padding: 15px 36px;
          }
          .channel {
            display: block;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
            margin-botto: 0.5em;
          }
          .channel img {
            width: 100%;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>
      </div>
    );
  }
}
