import React from 'react'
import Link from 'next/link';

export default function Header({headerText, nav = false}) {
  return (
    <header>
      <div className='actions-bar'>
        <h1>{headerText}</h1>

        <button>SEGUIR</button>
      </div>
      <Link href='/'>
        <a>
          <span className='material-icons'>arrow_back_ios</span>
        </a>
      </Link>


      <style jsx>{`
        button {
          color: white;
          background: black;
          border: none;
          padding: 8px 16px;
          border-radius: 10px;
          cursor: pointer;
        }
        button:hover {
          background: #d2d2d2;
        }
        header {
          color: black;
          background: #fff;
          padding: 0 36px;
          height: 100px;
          display: flex;
          font-weight: 700;
          align-items: flex-end;
          text-align: left;
          position: relative;
        }
        header a {
          color: black;
          font-size: 32px;
          position: sticky;
          top: 0;
          text-decoration: none;
        }
      `}</style>
    </header>
  );
}
