import 'isomorphic-fetch';
import Link from 'next/link';
import AudioControls from '../../components/AudioControls';
import {MdKeyboardArrowDown} from 'react-icons/md'

export async function getServerSideProps(context) {
  let idPodcast = context.query.podcast;

  let req = await fetch(
    `https://api.audioboom.com/audio_clips/${idPodcast}.mp3?version=2`
  );

  let data = await req.json();
  let clip = data.body.audio_clip;

  return { props: { clip } };
}

export default function podcast({ clip }) {
  return (
    <div>
      <header>Podcast</header>

      <div className='modal'>
        <div className='clip'>
          <nav>
            <Link
              href={`/[channel]?id=${clip.channel.id}`}
              as={`/${clip.channel.id}`}
            >
              <a className='close'>&lt; Volver</a>
            </Link>
          </nav>

          <picture>
            <div
              style={{
                backgroundImage: `url(${
                  clip.urls.image || clip.channel.urls.logo_image.original
                })`,
              }}
            />
          </picture>

          <div className='player'>
            <h3>{clip.title}</h3>
            <h6>{clip.channel.title}</h6>
            <AudioControls clipSrc={clip.urls.high_mp3} />
          </div>
        </div>
      </div>

      <style jsx>{`
        nav {
          background: none;
        }
        nav a {
          display: inline-block;
          padding: 15px;
          color: white;
          cursor: pointer;
          text-decoration: none;
        }
        .active-episode {
          background-color: #131414;
          color: white;
          border-radius: 20px;
          padding: 16px 16px;
          margin: 0;
        }
        .clip {
          display: flex;
          height: 100%;
          flex-direction: column;
          background: #131414;
          color: white;
        }
        picture {
          display: flex;
          align-items: center;
          justify-content: center;
          flex: 1 1;
          flex-direction: column;
          width: auto;
          padding: 10%;
        }
        picture div {
          width: 100%;
          height: 100%;
          background-position: 50% 50%;
          background-size: contain;
          background-repeat: no-repeat;
        }
        .player {
          padding: 30px;
          text-align: center;
        }
        h3 {
          margin: 0;
          font-size: 16px;
        }
        h6 {
          margin: 0;
          margin-top: 1em;
          font-size: 12px;
          font-weight: 400;
        }
        audio {
          margin-top: 2em;
          width: 100%;
        }

        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 99999;
        }
      `}</style>

      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
        body {
          margin: 0;
          background: #fff;
          font-family: 'Roboto', sans-serif;
        }
      `}</style>
    </div>
  );
}
