import React from 'react'

export default function TranslucentImage({urls}) {
  return (
    <div>
      <img
        src={clip.urls.image || clip.channel.urls.logo_image.original}
        alt=''
        srcSet=''
      />
      <div
        className='banner-shadow'
        style={{
          backgroundImage: `url(${
            clip.urls.image || clip.channel.urls.logo_image.original
          })`,
        }}
      ></div>

      <style jsx>{`
        img {
          width: 100%;
          border-radius: 20px;
          filter: blur(30px);
        }
        .banner-shadow {
          position: absolute;
          top: 0;
          margin: 0;
          width: 100%;
          height: 100%;
          border-radius: 20px;
          opacity: 1;
          z-index: 9999;
        }
      `}</style>
    </div>
  );
}
