import React from 'react'
import Link from 'next/link'
import {useRouter} from 'next/router'

export default function EpisodeList ({audioClips, channel}) {
  console.log(audioClips.channel);

  return (
    <div>
      {audioClips.map((clip) => (
        <Link 
          href={`/[channel]/[podcast]?id=${clip.id}`}
          as={`/channel/${clip.id}`}
          key={clip.id}
        >
          <a className='podcast'>
            <h3>{clip.title}</h3>
            <div className='meta'>{Math.ceil(clip.duration / 60)} minutes</div>
            {clip.title}
          </a>
        </Link>
      ))}

      <style jsx>{`
        .podcast {
          display: block;
          text-decoration: none;
          color: #333;
          padding: 15px;
          border-bottom: 1px solid rgba(0, 0, 0, 0.2);
          cursor: pointer;
        }
        .podcast:hover {
          color: #000;
        }
        .podcast h3 {
          margin: 0;
        }
        .podcast .meta {
          color: #666;
          margin-top: 0.5em;
          font-size: 0.8em;
        }
      `}</style>
    </div>
  );
}
