import React from 'react'
import { MdPause, MdSkipPrevious, MdSkipNext } from 'react-icons/md';

export default function AudioControls({clipSrc}) {
  return (
    <div>
      <div className='player'>
        <audio autoPlay={false}>
          <source src={clipSrc} type='audio/mpeg' />
        </audio>

        <div className='main-player'>
          <button className='controller-button'>
            <MdSkipPrevious color='white' size='3em' />
          </button>
          <button className='play-button'>
            <MdPause color='white' size='3em' />
          </button>
          <button className='controller-button'>
            <MdSkipNext color='white' size='3em' />
          </button>
        </div>

        <div className='control-bar'>
          <div className='progress-bar'>
            <div className='payback-controller' />
          </div>
        </div>

        <div className='progress-time'>
          <div>0:43</div>
          <div>0:43</div>
        </div>
      </div>

      <style jsx>{`
        .controller-button {
          background: none;
        }
        .progress-time {
          display: flex;
          justify-content: space-between;
          color: #979797;
          font-size: 12px;
        }
        .control-bar {
          height: 4px;
          background: #5e5f5f;
          width: 100%;
          position: relative;
          margin: 16px 0;
        }
        .progress-bar {
          position: absolute;
          height: 4px;
          background: #ffffff;
          left: 0;
          right: 20%;
        }
        .payback-controller {
          position: absolute;
          height: 20px;
          width: 20px;
          border-radius: 50%;
          border: 4px solid #131414;
          right: 0;
          top: 0;
          background: #fff;
          margin: -12px -12px;
        }
        .main-player {
          display: flex;
          justify-content: space-around;
          align-items: center;
          margin-bottom: 56px;
        }
        .player {
          height: 156px;
        }
        .play-button {
          width: 72px;
          height: 72px;
          border-radius: 50%;
          border: 1px solid white;
          background: none;
        }
      `}</style>
    </div>
  );
}
