import React from 'react'
import Link from 'next/link'
import Router, {useRouter} from 'next/router';

export default function EpisodeList ({audioClips}) {
  const router = useRouter();
  const onPodcastClick = (podcastId, channelId) => {
    router.push(
      {
        pathname: `/[channel]/[podcast]`,
        query: { podcastId: podcastId },
        as: `/${channelId}/${podcastId}`,
      },
      `/${channelId}/${podcastId}`
    );
    console.log(router.query);

  }
  return (
    <div>
      {audioClips.map((clip) => (
        <div onClick={() => onPodcastClick(clip.id, clip.channel.id)} key={clip.id}>
          <h3>{clip.title}</h3>
          <div className='meta'>{Math.ceil(clip.duration / 60)} minutes</div>
          {clip.title}
        </div>
      ))}

      <style jsx>{`
        .podcast {
          display: block;
          text-decoration: none;
          color: #333;
          padding: 15px 36px;
          border-bottom: 1px solid rgba(0, 0, 0, 0.2);
          cursor: pointer;
        }
        .podcast:hover {
          color: #000;
        }
        .podcast h3 {
          margin: 0;
        }
        .podcast .meta {
          color: #666;
          margin-top: 0.5em;
          font-size: 0.8em;
        }
      `}</style>
    </div>
  );
}
