import React, { useState } from 'react'
import {MdSort} from 'react-icons/md'

export default function FiltersIcon() {
  const [showDropdown, setShowDropdown] = useState(false)
  return (
    <div className='sort'>
      <div onClick={() => showDropdown ? setShowDropdown(false) : setShowDropdown(true)}>
        <MdSort size='1.5em' className='icon'/>
      </div>
      <div className='sort-dropdown'>
        <button>Más Popuares</button>
        <button className='active'>Fecha de subida (más antiguos)</button>
        <button>Fecha de subida (más recientes)</button>
      </div>

      <style jsx>{`
        .sort {
          position: relative;
        }
        .sort-dropdown {
          display: ${showDropdown ? 'block' : 'none'};
          border: 1px solid #aaa;
          border-radius: 20px;
          background: #fff;
          padding: 15px 10px;
          position: absolute;
          right: 0;
          width: 15em;
          text-align: right;
          transition: .6s;
        }
        button {
          outline: none;
          text-align: left;
          font-size: .9em;
          padding: 10px 10px;
          width: 100%;
          background: transparent;
          border: none;
        }
        .active {
          padding: 10px 10px;
          color: white;
          background: #131414;
          border-radius: 10px;
        }
        .icon {
          right: 0;
          float: right;
        }
      `}</style>
    </div>
  );
}
