import 'isomorphic-fetch';
import Link from 'next/link';
import AudioControls from '../../components/AudioControls';
import { MdKeyboardArrowLeft } from 'react-icons/md';
import TranslucentImage from '../../components/TranslucentImage';
import { useContext, useState, useEffect } from 'react';
import { PlayerContext } from '../../contexts/PlayerContext';
import { useRouter } from 'next/router';

export async function getServerSideProps(context) {
  let idChannel = context.query.channel;

  let req = await fetch(
    `https://api.audioboom.com/channels/${idChannel}/audio_clips?version=2`
    );

  let data = await req.json()
  let audioClips = data.body.audio_clips;

  return {props: {audioClips}}
}

export default function podcast({ audioClips }) {
  const router = useRouter();
  const [loading, setLoading] = useState(true)

  const {
    SetCurrentIndex,
    SetPlaylist,
    SetCurrent,
    currentSong,
    currentSongIndex,
    playing,
    togglePlaying,
    playlist
  } = useContext(PlayerContext);

  useEffect(() => {
    const setContextState = async () => {
      if (playlist === null) {
        SetPlaylist(audioClips)
      }
      for (let i = 0; i < audioClips.length; i++) {
        if (router.query.podcast == audioClips[i].id) {
          await SetCurrentIndex(i)
          await SetCurrent(audioClips[i])
          setLoading(false)
        }
      }
    }
    setContextState()
  }, [])

  useEffect(() => {
    const updateContextState = async () => {
      setLoading(true)
      for (let i = 0; i < playlist.length; i++) {
          if (currentSongIndex == playlist[i].id) {
            await SetCurrent(playlist[i])
            setLoading(false)
          }
        }
    }
    updateContextState()
  }, [currentSongIndex])


  if (loading) {
    return <div className="modal">...</div>
  }

  return (
    <div>
      <div className='modal'>
        <div className='clip'>
          <nav>
            <Link
              href={`/[channel]?id=${currentSong.channel.id}`}
              as={`/${currentSong.channel.id}`}
            >
              <a>
                <MdKeyboardArrowLeft size='24px' />
              </a>
            </Link>
          </nav>

          <div className='img-container'>
            <TranslucentImage
              clipUrls={currentSong.urls}
              channelUrls={currentSong.channel.urls}
            />
          </div>

          <div className='info'>
            <h3>{currentSong.title}</h3>
            <h6>{currentSong.channel.title}</h6>
          </div>

          <AudioControls clipSrc={currentSong.urls.high_mp3} />
        </div>
      </div>

      <style jsx>{`
        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          overflow-y: auto;
          z-index: 99999;
          background: #131414;
        }
        .clip {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          padding: 2em 2em;
          height: 85%;
          min-height: 600px;
          max-height: 750px;
          top: 0;
          color: white;
        }
        nav {
          padding: 0;
          background: none;
        }
        nav a {
          color: white;
          cursor: pointer;
        }
        .img-container {
          width: 80%;
          max-width: 250px;
          margin: 0 auto;
          position: relative;
        }
        .info {
          width: 90%;
          margin: 1em auto;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
          text-align: center;
        }
        h3 {
          margin: 8px 0;
          font-size: 16px;
        }
        h6 {
          margin: 0;
          font-size: 12px;
          font-weight: 400;
          color: #a8a8a8;
          text-transform: uppercase;
        }
      `}</style>

      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
        body {
          margin: 0;
          background: #fff;
          font-family: 'Roboto', sans-serif;
        }
      `}</style>
    </div>
  );
}
