import PodcastCover from './PodcastCover';
import FiltersIcon from './FiltersIcon';

export default function PodcastGrid({channels, title, favs = true}) {
  return (
    <div>
      <div className='header-channels'>
        <p>TUS SEGUIDOS</p>
      </div>

      {favs && (
        <div className='channels-favs'>
          {channels.map((channel, index) => (
            <PodcastCover channel={channel} index={index} />
          ))}
        </div>
      )}

      <div className='header-channels'>
        <p>{title}</p>
        <FiltersIcon />
      </div>

      <div className='channels'>
        {channels.map((channel, index) => (
          <PodcastCover channel={channel} index={index} />
        ))}
      </div>

      <style jsx>{`
        .channels-favs {
          display: grid;
          grid-gap: 42px 10px;
          padding: 24px 0;
          border-bottom: 1px solid #e2e2e2;
          grid-auto-flow: column;
          grid-auto-columns: repeat(auto-fill, minmax(145px, 1fr));
          overflow-x: auto;
          scrollbar-width: none;
        }
        .header-channels {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 30px 0;
          border-bottom: 1px solid #e2e2e2;
        }
        p {
          font-size: 12px;
          color: black;
          font-weigth: 400;
          text-transform: uppercase;
          margin: 0;
        }
        .channels {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(145px, 1fr));
          grid-gap: 42px 10px;
          padding: 24px 0;
          border-bottom: 1px solid #e2e2e2;
        }
      `}</style>
    </div>
  );
}
