import React from 'react'
import {MdSort} from 'react-icons/md'

export default function FiltersIcon() {
  return (
    <div className='sort'>
      <MdSort size='1.5em' className='icon'/>
      <div className='sort-dropdown'>
        <button>Más Popuares</button>
        <button>Fecha de subida (más antiguos)</button>
        <button>Fecha de subida (más recientes)</button>
      </div>

      <style jsx>{`
        .sort {
          position: relative;
        }
        .sort-dropdown {
          border: 1px solid #aaa;
          border-radius: 20px;
          background: #fff;
          padding: 15px 20px;
          position: absolute;
          right: 0;
          width: 14em;
        }
        button {
          text-align: left;
          font-size: .8em;
          padding: 10px 0;
          width: 100%;
          border-bottom: 1px solid #aaa;
          background: transparent;
        }
        .icon {
          position: absolute;
          right: 0;
          transform: rotate(180deg);
        }
      `}</style>
    </div>
  );
}
