export default function TranslucentImage({clipUrls, channelUrls}) {
  return (
    <div>
      <img
        src={clipUrls.image || channelUrls.logo_image.original}
        alt=''
        srcSet=''
      />
      <div
        className='banner-shadow'
        style={{
          backgroundImage: `url(${
            clipUrls.image || channelUrls.logo_image.original
          })`,
        }}
      ></div>

      <style jsx>{`
        img {
          width: 100%;
          border-radius: 20px;
        }
        .banner-shadow {
          filter: blur(30px);
          position: absolute;
          top: 0;
          margin: 0;
          width: 100%;
          height: 100%;
          border-radius: 20px;
          opacity: .8;
          z-index: -1;
        }
      `}</style>
    </div>
  );
}
