import 'isomorphic-fetch';
import Link from 'next/link';
import AudioControls from '../../components/AudioControls';
import { MdKeyboardArrowLeft } from 'react-icons/md';
import TranslucentImage from '../../components/TranslucentImage';
import { useContext, useState, useEffect } from 'react';
import { PlayerContext } from '../../contexts/PlayerContext';
import { useRouter } from 'next/router';

export async function getServerSideProps(context) {
  let idChannel = context.query.channel;

  let req = await fetch(
    `https://api.audioboom.com/channels/${idChannel}/audio_clips?version=2`
    );

  let data = await req.json()
  let audioClips = data.body.audio_clips;

  return {props: {audioClips}}

  // let idPodcast = context.query.podcast;
  // let req = await fetch(
  //   `https://api.audioboom.com/audio_clips/${idPodcast}.mp3?version=2`
  // );

  // let data = await req.json();
  // let clip = data.body.audio_clip;

  // return { props: { clip } };
}

export default function podcast({ audioClips }) {
  const router = useRouter();

  const {
    SetCurrentIndex,
    SetPlaylist,
    SetCurrent,
    currentSong,
    playlist
  } = useContext(PlayerContext);

  useEffect(() => {
    if (playlist === null) {
      SetPlaylist(audioClips)
    }
    for (let i = 0; i < audioClips.length; i++) {
      if (router.query.podcast == audioClips[i].id) {
        SetCurrentIndex(i)
        SetCurrent(audioClips[i])
      }
    }
  }, [])

  return (
    <div>
      <div className='modal'>
        {/* <div className='clip'>
          <nav>
            <Link
              href={`/[channel]?id=${clip.channel.id}`}
              as={`/${clip.channel.id}`}
            >
              <a>
                <MdKeyboardArrowLeft size='24px' />
              </a>
            </Link>
          </nav>

          <div className='img-container'>
            <TranslucentImage
              clipUrls={clip.urls}
              channelUrls={clip.channel.urls}
            />
          </div>

          <div className='info'>
            <h3>{clip.title}</h3>
            <h6>{clip.channel.title}</h6>
          </div>

          <AudioControls clipSrc={clip.urls.high_mp3} /> */}
        {/* </div> */}
      </div>

      <style jsx>{`
        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          overflow-y: auto;
          z-index: 99999;
          background: #131414;
        }
        .clip {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          padding: 2em 2em;
          height: 85%;
          min-height: 600px;
          max-height: 750px;
          top: 0;
          color: white;
        }
        nav {
          padding: 0;
          background: none;
        }
        nav a {
          color: white;
          cursor: pointer;
        }
        .img-container {
          width: 80%;
          max-width: 250px;
          margin: 0 auto;
          position: relative;
        }
        .info {
          width: 90%;
          margin: 1em auto;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
          text-align: center;
        }
        h3 {
          margin: 8px 0;
          font-size: 16px;
        }
        h6 {
          margin: 0;
          font-size: 12px;
          font-weight: 400;
          color: #a8a8a8;
          text-transform: uppercase;
        }
      `}</style>

      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
        body {
          margin: 0;
          background: #fff;
          font-family: 'Roboto', sans-serif;
        }
      `}</style>
    </div>
  );
}
