import {useContext} from 'react'
import {useRouter} from 'next/router';
import FiltersIcon from './FiltersIcon';
import {PlayerContext} from '../contexts/PlayerContext'
import {MdVolumeUp} from 'react-icons/md'

export default function EpisodeList ({audioClips}) {
  const { SetCurrent, currentSong} = useContext(PlayerContext)

  const router = useRouter();
  console.log(audioClips[0]);



  const onPodcastClick = (podcastId, channelId) => {
    SetCurrent(podcastId);
    router.push(
      {
        pathname: `/[channel]/[podcast]`,
        query: { podcastId: podcastId },
        as: `/${channelId}/${podcastId}`,
      },
      `/${channelId}/${podcastId}`
    );
    console.log(router.query);

  }
  return (
    <div>
      <div className='episodes-header'>
        <h3>{`${audioClips.length} EPISODIOS`}</h3>
        <FiltersIcon />
      </div>

      {audioClips.map((clip, index) => (
        <div
          className={'episode ' + (currentSong === clip.id ? 'active' : '')}
          onClick={() => onPodcastClick(clip.id, clip.channel.id)}
          key={clip.id}
        >
          <h2>{clip.title}</h2>
          <div className='meta'>{Math.ceil(clip.duration / 60)} minutes</div>
          <p>{clip.title}</p>
          {currentSong === clip.id && (
            <span>
              <MdVolumeUp size='2em' color='green' />
            </span>
          )}
        </div>
      ))}

      <style jsx>{`
        .episodes-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 30px 0;
          text-align: left;
          border-bottom: 1px solid #e2e2e2;
        }
        h3 {
          font-size: 12px;
          font-weight: 600;
          margin: 0;
        }
        .episode {
          display: block;
          text-decoration: none;
          margin: 0 0.6em;
          padding: 16px 0;
          border-bottom: 1px solid #e2e2e2;
          cursor: pointer;
        }
        .active {
          background: black;
          color: white;
          margin: 0;
          padding: 16px 1em;
          border-bottom: none;
          border-radius: 20px;
          postion: relative;
        }
        .active h2,
        .active p {
          width: 70%;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
          text-align: center;
        }
        .active span {
          position: absolute;
          right: 0;
        }
        .episode:hover {
          color: #000;
        }
        h2 {
          margin: 16px 0;
          font-size: 16px;
        }
        .episode .meta {
          color: #979797;
          font-size: 12px;
          margin-bottom: 8px;
        }
        p {
          color: #979797;
          font-size: 12px;
          margin: 0;
        }
      `}</style>
    </div>
  );
}
