import 'isomorphic-fetch'

export default class extends React.Component {

  static async getInitialProps() {
    let req = await fetch('https://api.audioboom.com/channels/recommended?api_version=2')
    let { body: channels } = await req.json();

    return {channels}
  }

  render() {
    const { channels } = this.props;
    return (
      <div>
        <header>Podcast</header>

        <div className="channels">
          {channels.map(channel => (
            <div className="channel">
              <img src={ channel.urls.logo_image.original } alt="" />
              <h2>{ channel.title }</h2>
            </div>
          ))}
        </div>

        <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, min-max(160px, 1fr));
          }
          .channel {
            display: block;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
            margin-botto: 0.5em;
          }
          .channel img {
            width: 100%;
          }
          h2{
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>

        <style jsx global>{`
            body {
              margin: 0;
              background: #fff;
              font-family: system-ui;
            }
        `}</style>
      </div>
    )
  }
}
