import React from 'react'
import Link from 'next/link';

export default function Header({headerText, nav = true, serie = true, seguir = true}) {
  return (
    <header>
      {nav && (
        <div className='actions-bar'>
          <Link href='/'>
            <a>
              <span className='material-icons'>arrow_back_ios</span>
            </a>
          </Link>

          {seguir && <button>SEGUIR</button>}
        </div>
      )}
      {serie && <p>{`SERIE DE POSTA`}</p>}

      <h1>{headerText}</h1>

      <style jsx>{`
        .actions-bar {
          maring-bottom: 30px;
          width: 100%;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }
        button {
          color: white;
          background: black;
          border: none;
          padding: 8px 16px;
          border-radius: 10px;
          cursor: pointer;
        }
        button:hover {
          background: #d2d2d2;
        }
        h1 {
          margin: 8px 0;
        }
        p {
          margin: 0;
          font-size: 12px;
        }
        header {
          margin-top: 64px;
          color: black;
          background: #fff;
          padding: 0 36px;
          height: auto;
          display: block;
          font-weight: 700;
          position: relative;
        }
        header a {
          color: black;
          font-size: 32px;
          position: sticky;
          top: 0;
          text-decoration: none;
        }
      `}</style>
    </header>
  );
}
