import {MdPause} from 'react-icons/md'
export default function Player() {
  return (
    <div>
      <div className='player'>
        <div className='img-container'>
          <div
            className='banner-shadow'
            style={{
              backgroundImage: `url(https://images.theabcdn.com/i/33439910)`,
            }}
          >
            <img src='https://images.theabcdn.com/i/33439910' alt='' srcset='' />

          </div>
        </div>
        <div className='info'>
          <h3>Teaser: Juro que es Posta</h3>
          <h4>Posta</h4>
        </div>
        <MdPause size='2em' color='white' />
      </div>

      <style jsx>{`
        .player {
          background: #131414;
          position: fixed;
          right: 0;
          left: 0;
          bottom: 0;
          border-radius: 20px 20px 0 0;
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 0 24px;
        }
        .info{
          margin-left: -2em;
        }
        .img-container{
          width: 64px;
          position: relative;
        }
        img {
          width: 100%;
          border-radius: 20px;
          filter: blur(20px);
        }
        .banner-shadow {
          position: absolute;
          top: 0;
          margin: 0;
          width: 100%;
          height: 64px;
          border-radius: 20px;
          z-index: -1;
          opacity: 0.5;
        }
        h3 {
          color: #fff;
          font-size: 16px;
          font-weight: bold;
          margin: 0 0 8px;
        }
        h4 {
          color: #a8a8a8;
          font-size: 12px;
          font-weight: 400;
          margin: 0;
          text-transform: uppercase;
        }
      `}</style>
    </div>
  );
}
