import { createContext, useReducer } from "react";
import {ADD_TO_FOLLOW, REMOVE_FROM_FOLLOW} from './types'
import {FavsReducer} from './FavsReducer'

export const FavsContext = createContext()

export const FavsState = props => {
  const initialState = {
    myList: []
  }

  const [state, dispatch] = useReducer(FavsReducer, initialState)

  const Follow = id => dispatch({type: ADD_TO_FOLLOW, data: id});

  const Unfollow = id => dispatch({type: REMOVE_FROM_FOLLOW, data: id})

  return (
    <FavsContext.Provider 
      value={{
        myList: state.myList,
        Follow,
        Unfollow
      }}
    >
      {props.children}
    </FavsContext.Provider>
  )

}
