import React, {useContext} from 'react'
import Head from 'next/head'
import Link from 'next/link'


export default function Layout ({children, title, headerText}) {
  // const {player, playerStore} = useContext(PlayerContext)
  return (
    <div>
      <Head>
        <title>{title}</title>
        <meta name='viewport' content='initial-scale=1.0, width=device-width' />
        <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"></link>
      </Head>

      <header>
        <Link href='/'>
          <a>{headerText}</a>
        </Link>
      </header>

      <div className='content'>
        {children}
      </div>


      {/* {
        player ? <Player /> : null
      } */}

      <style jsx>{`
        .content {
          padding: 0 36px;
        }
        header {
          color: black;
          background: #fff;
          padding: 0 36px;
          height: 100px;
          display: flex;
          font-weight: 700;
          align-items: flex-end;
          text-align: left;
          position: relative;
        }
        header a {
          color: black;
          font-size: 32px;
          position: sticky;
          top: 0;
          text-decoration: none;
        }
      `}</style>

      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
        body {
          margin: 0;
          background: #fff;
          font-family: 'Roboto', sans-serif;
        }
      `}</style>
    </div>
  );
}
