import Link from 'next/link';

export default function PodcastCover({channel, index}) {
  return (
    <div>
      <Link
        href={`/[channel]?id=${channel.id}`}
        key={index}
        as={`${channel.id}`}
      >
        <a className='channel'>
          <img src={channel.urls.logo_image.original} alt='' />
          <h2>{channel.title}</h2>
        </a>
      </Link>

      <style jsx>{`
        .channel {
          display: block;
          border-radius: 3px;
          margin-botto: 0.5em;
        }
        img {
          width: 100%;
          box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
          border-radius: 20px;
        }
        a {
          color: black;
          text-decoration: none;
        }
        h2 {
          padding: 5px;
          font-size: 16px;
          font-weight: 400;
          margin: 0;
          text-align: center;
        }
      `}</style>
    </div>
  );
}
