import 'isomorphic-fetch';
import Link from 'next/link';
import AudioControls from '../../components/AudioControls';
import {MdKeyboardArrowDown} from 'react-icons/md'

export async function getServerSideProps(context) {
  let idPodcast = context.query.podcast;

  let req = await fetch(
    `https://api.audioboom.com/audio_clips/${idPodcast}.mp3?version=2`
  );

  let data = await req.json();
  let clip = data.body.audio_clip;

  return { props: { clip } };
}

export default function podcast({ clip }) {
  return (
    <div>
      <header>Podcast</header>

      <div className='modal'>
        <div className='clip'>
          <nav>
            <Link
              href={`/[channel]?id=${clip.channel.id}`}
              as={`/${clip.channel.id}`}
            >
              <MdKeyboardArrowDown size='24px' />
            </Link>
          </nav>

          <div className='img-container'>
            <img
              src={clip.urls.image || clip.channel.urls.logo_image.original}
              alt=''
              srcSet=''
            />
          </div>
          {/* <div
              className='banner-shadow'
              style={{
                backgroundImage: `url(${
                  clip.urls.image || clip.channel.urls.logo_image.original
                })`,
              }}
            /> */}
          <div className='info'>
            <h3>{clip.title}</h3>
            <h6>{clip.channel.title}</h6>
          </div>
          <div className='player'>
            <AudioControls clipSrc={clip.urls.high_mp3} />
          </div>
        </div>
      </div>

      <style jsx>{`
        .info {
          margin: 48px 0;
          text-align: center;
        }
        .banner-shadow {
          position: absolute;
          top: 0;
          margin: 0;
          width: 100%;
          height: 300px;
          border-radius: 20px;
          filter: blur(30px);
          opacity: 1;
          z-index: 2;
        }
        .img-container {
          width: 300px;
          margin: auto;
          position: relative;
        }
        img {
          width: 100%;
          z-index: 3;
          border-radius: 20px;
        }
        nav {
          background: none;
        }
        nav a {
          display: inline-block;
          padding: 15px;
          color: white;
          cursor: pointer;
          text-decoration: none;
        }
        .active-episode {
          background-color: #131414;
          color: white;
          border-radius: 20px;
          padding: 16px 16px;
          margin: 0;
        }
        .clip {
          display: flex;
          height: 100%;
          flex-direction: column;
          background: #131414;
          color: white;
          z-index: -1;
        }

        .player {
          padding: 30px;
          text-align: center;
        }
        h3 {
          margin: 8px 0;
          font-size: 16px;
        }
        h6 {
          margin: 0;
          margin-top: 1em;
          font-size: 12px;
          font-weight: 400;
          color: #a8a8a8;
        }
        audio {
          margin-top: 2em;
          width: 100%;
        }

        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 99999;
        }
      `}</style>

      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
        body {
          margin: 0;
          background: #fff;
          font-family: 'Roboto', sans-serif;
        }
      `}</style>
    </div>
  );
}
