import React, { useContext, useRef, useState, useEffect } from 'react'
import { MdPause, MdPlayArrow, MdSkipPrevious, MdSkipNext } from 'react-icons/md';
import {PlayerContext} from '../contexts/PlayerContext'

export default function AudioControls({clipSrc}) {
  const {
    currentSong,
    playing,
    togglePlaying
  } = useContext(PlayerContext)

  const audio = useRef('audio_tag')

  const [stateVolume, setStateVolume] = useState(0.5);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);

  const fmtMSS = (s) => {
    return (s - (s %= 60)) / 60 + (9 < s ? ':' : ':0') + ~~s;
  };

  const toggleAudio = () =>
    audio.current.paused ? audio.current.play() : audio.current.pause();

  useEffect(() => {
    audio.current.volume = stateVolume
    if (playing) {
      toggleAudio();
    }
  }, [currentSong])

  return (
    <div className='player'>
      <audio autoPlay={false}>
        <source
          ref={audio}
          preload='true'
          src={clipSrc} 
          type='audio/mpeg' 
        />
      </audio>

      <div className='main-player'>
        <button className='controller-button'>
          <MdSkipPrevious color='white' size='3em' />
        </button>
        <button className='play-button' onClick={()=>{toggleAudio(); togglePlaying();}} >

          <MdPlayArrow color='white' size='3em' />
        </button>
        <button className='controller-button'>
          <MdSkipNext color='white' size='3em' />
        </button>
      </div>

      <input type='range' name='progressbar' id='pgrbar' className='slider' />
      {/* <div className='control-bar'>
        <div className='progress-bar'>
          <div className='payback-controller' />
        </div>
      </div> */}

      <div className='progress-time'>
        <div>0:43</div>
        <div>0:43</div>
      </div>

      <style jsx>{`
        .slider {
          -webkit-appearance: none;
          height: 4px;
          background-color: #5e5f5f;
          width: 100%;
          outline: none;
          overflow: hidden;
          -webkit-transition: 0.2s;
          transition: opacity 0.2s;
        }

        .slider::-webkit-slider-thumb {
          -webkit-appearance: none;
          appearance: none;
          width: 2.5em;
          height: 2.5em;
          border-radius: 50%;
          border: 4px solid #131414;
          cursor: pointer;

          background: #fff;
          box-shadow: -210px 0 0 200px #fff;
        }

        .slider::-webkit-slider-runnable-track {
          -webkit-appearance: none;
        }

        .player {
          height: auto;
        }
        .main-player {
          display: flex;
          justify-content: space-around;
          align-items: center;
          margin-bottom: 10%;
        }
        .controller-button {
          background: none;
          border: none;
        }
        .play-button {
          width: 72px;
          height: 72px;
          border-radius: 50%;
          border: 1px solid white;
          background: none;
        }
        .control-bar {
          height: 4px;
          background: #5e5f5f;
          width: 100%;
          position: relative;
          margin: 16px 0;
        }
        .progress-bar {
          position: absolute;
          height: 4px;
          background: #ffffff;
          left: 0;
          right: 20%;
        }
        .payback-controller {
          position: absolute;
          height: 20px;
          width: 20px;
          border-radius: 50%;
          border: 4px solid #131414;
          right: 0;
          top: 0;
          background: #fff;
          margin: -12px -12px;
        }
        .progress-time {
          display: flex;
          justify-content: space-between;
          color: #979797;
          font-size: 12px;
        }
      `}</style>
    </div>
  );
}
