export default function Player() {
  return (
    <div>
      <div className='player'>
        <div className='img-container'>
          <img src='https://images.theabcdn.com/i/21285758' alt='' srcset='' />
          <div
            className='banner-shadow'
            style={{
              backgroundImage: `url(https://images.theabcdn.com/i/21285758})`,
            }}
          />
        </div>
        <div>
          <h3>Teaser: Juro que es Posta</h3>
          <h4>Posta</h4>
        </div>
        <span className='material-icons'>pause</span>
      </div>

      <style jsx>{`
        .player {
          background: #131414;
          position: fixed;
          height: 120px;
          right: 0;
          left: 0;
          bottom: 0;
          border-radius: 20px 20px 0 0;
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 0 24px;
        }
        .img-container{
          width: 64px;
          position: relative;
        }
        img {
          width: 100%;
          border-radius: 20px;
        }
        .banner-shadow {
          margin: 0;
          position: absolute;
          top: 0;
          width: 100%;
          height: 64px;
          border-radius: 20px;
          background-size: cover;
          background-color: #aaa;
          filter: blur(15px);
          left: 50%;
          transform: translate(-50%);
          z-index: -1;
        }
        h3 {
          color: #fff;
          font-size: 16px
          font-weight: bold;
          margin: 0 0 8px;
        }
        h4 {
          color: #a8a8a8;
          font-size: 12px;
          font-weight: 400;
          margin: 0;
          text-transform: uppercase;
        }
      `}</style>
    </div>
  );
}
