import React from 'react'
import Link from 'next/link'
import Router, {useRouter} from 'next/router';

export default function EpisodeList ({audioClips}) {
  const router = useRouter();
  const onPodcastClick = (podcastId, channelId) => {
    router.push(
      {
        pathname: `/[channel]/[podcast]`,
        query: { podcastId: podcastId },
        as: `/${channelId}/${podcastId}`,
      },
      `/${channelId}/${podcastId}`
    );
    console.log(router.query);

  }
  return (
    <div>
      <h3>{`${audioClips.length} EPISODIOS`}</h3>

      {audioClips.map((clip) => (
        <div
          className='episode'
          onClick={() => onPodcastClick(clip.id, clip.channel.id)}
          key={clip.id}
        >
          <h2>{clip.title}</h2>
          <div className='meta'>{Math.ceil(clip.duration / 60)} minutes</div>
          {clip.title}
        </div>
      ))}

      <style jsx>{`
        h3 {
          padding: 30px 0;
          font-size: 0.9em;
          font-weight: 600;
          margin: 0;
          text-align: left;
          border-bottom: 1px solid #e2e2e2;
        }
        .episode {
          display: block;
          text-decoration: none;
          color: #333;
          margin: 0 16px;
          border-bottom: 1px solid rgba(0, 0, 0, 0.2);
          cursor: pointer;
        }
        .episode:hover {
          color: #000;
        }
        h2 {
          margin: 0;
          font-size: 16px;
        }
        .episode .meta {
          color: #666;
          margin-top: 0.5em;
          font-size: 0.8em;
        }
      `}</style>
    </div>
  );
}
