import { createContext, useReducer } from "react";
import {ADD_TO_FOLLOW, REMOVE_FROM_FOLLOW} from './types'
import {FavsReducer} from './FavsReducer'
import {useLocalStorage} from '../hooks/useLocalStorage'

export const FavsContext = createContext()

const [favs] = useLocalStorage('favs', false)

const initialValue = () => {
  if (favs !== undefined) {
    return favs
  }
  return []
}

export const FavsState = props => {
  const initialState = {
    myList: initialValue
  }

  const [state, dispatch] = useReducer(FavsReducer, initialState)

  const Follow = channel => dispatch({type: ADD_TO_FOLLOW, data: channel});

  const Unfollow = channel => dispatch({type: REMOVE_FROM_FOLLOW, data: channel})

  return (
    <FavsContext.Provider
      value={{
        myList: state.myList,
        Follow,
        Unfollow,
      }}
    >
      {props.children}
    </FavsContext.Provider>
  );

}
